function gamipress_points_exchanges_get_points_type_exchange_rates(points_type){return gamipress_points_exchanges_functions.rates[points_type]}
function gamipress_points_exchanges_get_user_points_balance(points_type){if(gamipress_points_exchanges_functions.user_points[points_type]===undefined){return 0}
return gamipress_points_exchanges_functions.user_points[points_type]}
function gamipress_points_exchanges_get_points_type_label(points_type){if(gamipress_points_exchanges_functions.points_types[points_type]===undefined){return ''}
return gamipress_points_exchanges_functions.points_types[points_type].plural_name}
function gamipress_points_exchanges_get_rate(from,to){var points_type_rates=gamipress_points_exchanges_get_points_type_exchange_rates(from);if(points_type_rates===undefined){return!1}
if(points_type_rates[to]===undefined){return!1}
return points_type_rates[to]}
function gamipress_points_exchanges_apply_rate(amount,from,to){var rate=gamipress_points_exchanges_get_rate(from,to);if(!rate){return 0}
return parseInt(amount*rate)}